INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(1, 'ANTH', '212', '\n      Anthropology : Processes of developmental change, as they affect small communities in the Third World and in unindustrialized parts of developed countries. Problems of technological change, political integration, population growth, industrialization, urban growth, social services, infrastructure and economic dependency.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(2, 'ARCH', '528', '\n      Architecture : Indigenous housing both transient and permanent, from the standpoint of individual structure and pattern of settlements. The principal historic examples of houses including housing in the age of industrial revolution and contemporary housing.    ', '(arch 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(3, 'BTEC', '502', '\n      Biotechnology : Examination of particular social and ethical challenges posed by modern biotechnology such as benefit sharing, informed consent in the research setting, access to medical care worldwide, environmental safety and biodiversity and the ethical challenges posed by patenting life.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(4, 'BUSA', '465', '\n      Business Admin : Concentrating on entrepreneurship and enterprise development, particular attention is given to the start-up, purchasing and management of small to medium-sized industrial firms. The focal point is in understanding the dilemmas faced by entrepreneurs, resolving them, developing a business plan and the maximum utilization of the financial, marketing and human resources that make for a successful operation.    ', '(facc 300||mgcr 341||intg 201)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(5, 'CCOM', '206', '\n      Communication (CCE) : Written and oral communication in Engineering (in English): strategies for generating, developing, organizing, and presenting ideas in a technical setting; problem-solving; communicating to different audiences; editing and revising; and public speaking. Course work based on academic, technical, and professional writing in engineering.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(6, 'CHEM', '110', '      Chemistry : A study of the fundamental principles of atomic structure, radiation and nuclear chemistry, valence theory, coordination chemistry, and the periodic table.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(7, 'CHEM', '120', '\n      Chemistry : A study of the fundamental principles of physical chemistry.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(8, 'CIVE', '469', '\n      Civil Engineering : Infrastructure systems, historical background and socio-economic impact; planning, organization, communication and decision support systems; budgeting and management; operations, maintenance, rehabilitation and replacement issues; public and private sectors, privatization and governments; infrastructure crisis and new technologies; legal, environmental, socio-economic and political aspects of infrastructure issues; professional ethics and responsibilities; case studies.    ', '(facc 300||mime 310)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(9, 'COMP', '202', '\n      Computer Science (Sci) : Introduction to computer programming in a high level language: variables, expressions, primitive types, methods, conditionals, loops. Introduction to algorithms, data structures (arrays, strings), modular software design, libraries, file input/output, debugging, exception handling. Selected topics.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(10, 'COMP', '206', '\n      Computer Science (Sci) : Comprehensive overview of programming in C, use of system calls and libraries, debugging and testing of code; use of developmental tools like make, version control systems.    ', '(comp 202||comp 250)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(11, 'COMP', '250', '\n      Computer Science (Sci) : Mathematical tools (binary numbers, induction, recurrence relations, asymptotic complexity, establishing correctness of programs), Data structures (arrays, stacks, queues, linked lists, trees, binary trees, binary search trees, heaps, hash tables), Recursive and non-recursive algorithms (searching and sorting, tree and graph traversal). Abstract data types, inheritance. Selected topics.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(12, 'COMP', '251', '\n      Computer Science (Sci) : Introduction to algorithm design and analysis. Graph algorithms, greedy algorithms, data structures, dynamic programming, maximum flows.    ', '(comp 250)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(13, 'COMP', '302', '\n      Computer Science (Sci) : Programming language design issues and programming paradigms. Binding and scoping, parameter passing, lambda abstraction, data abstraction, type checking. Functional and logic programming.    ', '(comp 250)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(14, 'COMP', '330', '\n      Computer Science (Sci) : Finite automata, regular languages, context-free languages, push-down automata, models of computation, computability theory, undecidability, reduction techniques.    ', '(comp 251)&&(math 240)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(15, 'COMP', '350', '\n      Computer Science (Sci) : Computer representation of numbers, IEEE Standard for Floating Point Representation, computer arithmetic and rounding errors. Numerical stability. Matrix computations and software systems. Polynomial interpolation. Least-squares approximation. Iterative methods for solving a nonlinear equation. Discretization methods for integration and differential equations.    ', '(math 222)&&(math 223)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(16, 'COMP', '360', '\n      Computer Science (Sci) : Advanced algorithm design and analysis. Linear programming, complexity and NP-completeness, advanced algorithmic techniques.    ', '(comp 251||comp 252)&&(math 240||math 235||math 363)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(17, 'COMP', '409', '\n      Computer Science (Sci) : Characteristics and utility of concurrent programs; formal methods for specification, verification and development of concurrent programs; communications, synchronization, resource allocation and management, coherency and integrity.    ', '(comp 251)&&(comp 302)&&(comp 310||ecse 427)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(18, 'COMP', '417', '\n      Computer Science (Sci) : This course considers issues relevant to the design of robotic and of intelligent systems. How can robots move and interact. Robotic hardware systems. Kinematics and inverse kinematics. Sensors, sensor data interpretation and sensor fusion. Path planning. Configuration spaces. Position estimation. Intelligent systems. Spatial mapping. Multi-agent systems. Applications.    ', '(comp 251)&&(math 223)&&(ecse 321||comp 206)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(19, 'COMP', '421', '\n      Computer Science (Sci) : Database Design: conceptual design of databases (e.g., entity-relationship model), relational data model, functional dependencies. Database Manipulation: relational algebra, SQL, database application programming, triggers, access control. Database Implementation: transactions, concurrency control, recovery, query execution and query optimization.    ', '(comp 206)&&(comp 251)&&(comp 302)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(20, 'COMP', '424', '\n      Computer Science (Sci) : Introduction to search methods. Knowledge representation using logic and probability. Planning and decision making under uncertainty. Introduction to machine learning.    ', '(comp 206||ecse 321)&&(math 323)&&(comp 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(21, 'COMP', '512', '\n      Computer Science (Sci) : Models and Architectures. Application-oriented communication paradigms (e.g. remote method invocation, group communication). Naming services. Synchronization (e.g. mutual exclusion, concurrency control). Fault-tolerance (e.g. process and replication, agreement protocols). Distributed file systems. Security. Examples of distributed systems (e.g. Web, CORBA). Advanced Topics.    ', '(comp 310)&&(comp 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(22, 'COMP', '520', '\n      Computer Science (Sci) : The structure of a compiler. Lexical analysis. Parsing techniques. Syntax directed translation. Run-time implementation of various programming language constructs. Introduction to code generation for an idealized machine. Students will implement parts of a compiler.    ', '(comp 273)&&(comp 302)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(23, 'COMP', '521', '\n      Computer Science (Sci) : Genre and history of games, basic game design, storytelling and narrative analysis, game engines, design of virtual worlds, real-time 2D graphics, game physics and physical simulation, pathfinding and game AI, content generation, 3D game concerns, multiplayer and distributed games, social issues.    ', '(comp 251)&&(math 223)&&(comp 303||comp 361)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(24, 'COMP', '525', '\n      Computer Science (Sci) : Propositional logic - syntax and semantics, temporal logic, other modal logics, model checking, symbolic model checking, binary decision diagrams, other approaches to formal verification.    ', '(comp 251)&&(comp 330)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(25, 'COMP', '529', '\n      Computer Science (Sci) : Development, analysis, and maintenance of software architectures, with special focus on modular decomposition and reverse engineering.    ', '(comp 303)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(26, 'COMP', '533', '\n      Computer Science (Sci) : Model-driven software development; requirements engineering based on use cases and scenarios; object-oriented modelling using UML and OCL to establish complete and precise analysis and design documents; mapping to Java. Introduction to meta-modelling and model transformations, use of modelling tools.    ', '(ecse 321||comp 303||comp 361)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(27, 'COMP', '557', '\n      Computer Science (Sci) : The study of fundamental mathematical, algorithmic and representational issues in computer graphics. The topics to be covered are: overview of graphics process, projective geometry, homogeneous coordinates, projective transformations, quadrics and tensors, line-drawing, surface modelling and object modelling reflectance models and rendering, texture mapping, polyhedral representations, procedural modelling, and animation.    ', '(math 223)&&(comp 251)&&(comp 206)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(28, 'COMP', '566', '\n      Computer Science (Sci) : Use of computer in solving problems in discrete optimization. Linear programming and extensions. Network simplex method. Applications of linear programming. Vertex enumeration. Geometry of linear programming. Implementation issues and robustness. Students will do a project on an application of their choice.    ', '(comp 360)&&(math 223)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(29, 'COMP', '575', '\n      Computer Science (Sci) : Study of a collection of algorithms that are basic to the world of concurrent programming. Discussion of algorithms from the following areas: termination detection, deadlock detection, global snapshots, clock synchronization, fault tolerance (byzantine and self-stabilizing systems). Students will implement algorithms on the BBN butterfly and will present papers on topics in these areas.    ', '(comp 310)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(30, 'ECON', '225', '\n      Economics (Arts) : A study of the application of economic theory to questions of environmental policy. Particular attention will be given to the measurement and regulation of pollution, congestion and waste and other environmental aspects of specific economies.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(31, 'ECON', '347', '\n      Economics (Arts) : The course focuses on the economic implications of, and problems posed by, predictions of global warming due to anthropogenic emissions of greenhouse gases. Attention is given to economic policies such as carbon taxes and tradeable emission permits and to the problems of displacing fossil fuels with new energy technologies.    ', '(econ 208)&&(econ 209||)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(32, 'ECSE', '200', '\n      Electrical Engineering : Circuit variables. Analysis of resistive circuits, network theorems (Kirchhoffâ€™s laws, Ohmâ€™s law, Norton and Thevenin equivalent). Ammeters, Voltmeters, and Ohmmeters. Analysis methods (nodal and mesh analysis, linearity, superposition). Dependent sources and Op-Amps. Energy storage elements. First and second order circuits.    ', '(phys 142)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(33, 'ECSE', '202', '\n      Electrical Engineering : Software development practices in the context of structured and object-oriented programming. Introduces the formalisms inherent in medium-to-large scale object-oriented programming. Introduction to tools and practices employed in commercial software development.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(34, 'ECSE', '205', '\n      Electrical Engineering : Probability: basic probability model, conditional probability, Bayes rule, random variables and vectors, distribution and density functions, common distributions in engineering, expectation, moments, independence, laws of large numbers, central limit theorem. Statistics: descriptive measures of engineering data, sampling distributions, estimation of mean and variance, confidence intervals, hypothesis testing, linear regression.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(35, 'ECSE', '210', '\n      Electrical Engineering : Second-order circuits. Sinusoidal sources and phasors. AC steady-state analysis. AC steady-state power. Laplace transform. Circuit analysis in the s-Domain. Two-port circuits. Elementary continuous signals, impulse functions, basic properties of continuous linear time-invariant (LTI) systems. Frequency analysis of continuous-time LTI systems.    ', '(ecse 200)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(36, 'ECSE', '211', '\n      Electrical Engineering : Engineering process: design specifications, parameters, optimization, implementation, troubleshooting and refinement; project management: scheduling, risk analysis, project control; case studies; design examples and project.    ', '(ecse 200)&&(ecse 202||comp 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(37, 'ECSE', '221', '\n      Electrical Engineering : Data representation in digital computers. Boolean algebra. Basic combinational circuits; their analysis and synthesis. Elements of sequential circuits: latches, flip-flops, counters and memory circuits. Computer structure, central processing unit, machine language. Assemblers and assembler language.    ', '(comp 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(38, 'ECSE', '222', '\n      Electrical Engineering : An introduction to digital logic, binary numbers and Boolean algebra, combinational circuits, optimized implementation of combinational circuits, arithmetic circuits, combinational circuit building blocks, flip-flops, registers, counters, design of digital circuits with VHDL, and synchronous sequential circuits.    ', '(ecse 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(39, 'ECSE', '223', '\n      Electrical Engineering : Integration of modelling with programming; abstraction in software engineering; structural modelling; state-based modelling; modelling of object-oriented systems, code generation; natural language constraints in modelling notations; architectural and design patterns; integrated development environments; programming tools (debugging, continuous build/integration, version control and code repositories, diff, defect and issue tracking, refactoring); code review processes.    ', '(ecse 202||comp 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(40, 'ECSE', '291', '\n      Electrical Engineering : Experiments with fundamental electric circuits illustrating the principles and limitations of basic electrical and electronic instrumentation in typical measurement applications. Introduction to basic electrical laboratory practice, design of experiments, and safety procedures. Introduction to error analysis and application to laboratory measurements. Design of electric circuits and characterization.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(41, 'ECSE', '305', '\n      Electrical Engineering : The basic probability model, the heuristics of model-building and the additivity of probability; classical models; conditional probability and Bayes rule; random variables and vectors, distribution and density functions, expectation; statistical independence, laws of large numbers, central limit theorem; introduction to random processes and random signal analysis.    ', '(ecse 303||ecse 306)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(42, 'ECSE', '306', '\n      Electrical Engineering : Review of complex functions. Discrete-and continuous-time signals, basic system properties. Linear time-invariant systems, convolution. Fourier series and Fourier transforms, frequency domain analysis, filtering, sampling. Laplace transforms and inversion, transfer functions, poles and zeros, solutions of linear constant-coefficient differential equations, transient and steady state response. Z-transforms.    ', '(ecse 210)&&(math 270||math 271)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(43, 'ECSE', '310', '\n      Electrical Engineering : An introduction to thermodynamics from the perspective of computer engineering. The first and second laws of thermodynamics; elementary information theory (bits, entropy); energy storage and dissipation in electrical circuits; effects of noise in switching circuits; the fluctuation-dissipation theorem; Landauerâ€™s principle; reversible and irreversible computation; energy costs of communication; thermal resistance, heat sinking, and cooling technologies for computing circuits.    ', '(ecse 200)&&(ecse 205)&&(ecse 222)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(44, 'ECSE', '316', '\n      Electrical Engineering : Introduction to the physical and software architecture of networks and the analysis and representation of signals; client-server and peer-to-peer architectures; layered design principles; network applications and socket programming; multimedia streaming, web transfer, and voice-over-IP; continuous-time and discrete-time signals; Fourier transforms and frequency domain analysis and representation of signals; filtering and sampling; flow and congestion control; solutions of linear constant-coefficient differential equations, transient and steady state response; Laplace transforms; addressing and routing for unicast, multicast, and broadcast transmission; wired and wireless access systems; multiple access protocols. Examples: Ethernet, http, TCP/IP, 802.11, OSPF, BGP.    ', '(comp 251)&&(ecse 200)&&(math 263)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(45, 'ECSE', '321', '\n      Electrical Engineering : Design, development and testing of software systems. Software life cycle: requirements analysis, software architecture and design, implementation, integration, test planning, and maintenance. The course involves a group project.    ', '(comp 202||comp 208||ecse 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(46, 'ECSE', '322', '\n      Electrical Engineering : Data structures (arrays, lists, stacks, queues, dequeues and trees) and their machine representation and simple algorithms. Peripheral devices: printers, keyboards, magnetic type drives, magnetic disc drives. Peripheral interfacing and busses. Introduction to operating systems. System integration. Computer systems and networks.    ', '(ecse 200||mech 383)&&(ecse 221)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(47, 'ECSE', '323', '\n      Electrical Engineering : Minimization and synthesis of combinational logic and finite state machines.Synthesis of synchronous and asynchronous sequential circuits. Principles of control design. Basic concepts in design for testability. Laboratory experiments involve the design and testing of FPGA based digital systems using CAD software and hardware description languages.    ', '(ccom 206||edec 206)&&(ecse 211)&&(ecse 221)&&(ecse 291)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(48, 'ECSE', '324', '\n      Electrical Engineering : Basic computer structures; instruction set architecture; assembly language; input/output; memory; software; processor implementation; computer arithmetic. Lab work involving assembly language level programming of single-board computers.    ', '(ecse 200)&&(ecse 222)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(49, 'ECSE', '325', '\n      Electrical Engineering : Design of digital systems. Implementation technologies; arithmetic modules; synthesis and advanced modelling techniques; verification; timing analysis; design for testability; asynchronous circuits; hardware/software co-design.    ', '(ecse 324)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(50, 'ECSE', '326', '\n      Electrical Engineering : Techniques for eliciting requirements; languages and models for specification of requirements; analysis and validation techniques, including feature-based, goal-based, and scenario-based analysis; quality requirements; requirements traceability and management; handling evolution of requirements; requirements documentation standards; requirements in the context of system engineering; integration of requirements engineering into software engineering processes.    ', '(ecse 223)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(51, 'ECSE', '404', '\n      Electrical Engineering : Modelling and simulation of control systems; basic concepts of linear systems; open and closed loop control; classical design of controllers - specifications in the step response and the frequency domain; state space design of controllers - pole placement and LQR; sampled data systems.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(52, 'ECSE', '411', '\n      Electrical Engineering : Communication system models; AM and FM modulation, performance of AM and FM systems in noise; sampling, PCM and DPCM techniques; FDM and TDM multiplexing systems; baseband digital transmission over bandlimited channels, digital modulation and detection techniques; illustrative examples of subscriber loop telephone systems, cable TV systems and broadcasting systems.    ', '(ecse 305)&&(ecse 304||ecse 306)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(53, 'ECSE', '412', '\n      Electrical Engineering : Discrete-time signals and systems; Fourier and Z-transform analysis techniques, the discrete Fourier transform; elements of FIR and IIR filter design, filter structures; FFT techniques for high speed convolution; quantization effects.    ', '(ecse 304||ecse 306)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(54, 'ECSE', '413', '\n      Electrical Engineering : Introduction to radio communications; satellite communication systems; the cellular concept; fading channel models, digital modulation techniques over fading channels, diversity systems, spread spectrum techniques; fixed assignment multiple access (FDMA, TDMA, CDMA), duplexing methods (FDD, TDD); illustrative examples of terrestrial mobile systems, fixed wireless systems, LEOs, etc.; overview of standardization activities.    ', '(ecse 411)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(55, 'ECSE', '414', '\n      Electrical Engineering : Introduction to the physical and software architecture of modern networks; circuit and packet switching; layered design principles; wired and wireless access systems; flow and congestion control; addressing and routing for unicast, multicast, and broadcast transmission; multiple access protocols; client-server and peer-to-peer architectures. Examples: Ethernet, TCP/IP, 802.11, ARQ, OSPF. BGP.    ', '(ecse 322)&&(ecse 304||ecse 306)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(56, 'ECSE', '415', '\n      Electrical Engineering : An introduction to the automated processing, analysis, and understanding of image data. Topics include image formation and acquisition, design of image features, image segmentation, stereo and motion correspondence matching techniques, feature clustering, regression and classification for object recognition, industrial and consumer applications, and computer vision software tools.    ', '(ecse 304||ecse 306)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(57, 'ECSE', '416', '\n      Electrical Engineering : Architecture and protocols of contemporary networks; wired and wireless access systems; flow and congestion control; network optimization; randomized multiple access protocols; queueing disciplines; low-power wireless networks. Examples: Ethernet, TCP/IP, 802.11, 802.15.4. Lab experiments addressing routing protocols, TCP, queuing disciplines and quality-of-service, and network security.    ', '(comp 250)&&(ecse 205)&&(ecse 308||ecse 316)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(58, 'ECSE', '420', '\n      Electrical Engineering : Modern parallel computing architectures for shared memory, message passing and data parallel programming models. The design of cache coherent shared memory multiprocessors. Programming techniques for multithreaded, message passing and distributed systems. Use of modern programming languages and parallel programming libraries.    ', '(ecse 427)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(59, 'ECSE', '421', '\n      Electrical Engineering : Definition, structure and properties of embedded systems. Real-time programming: interrupts, latency, context, re-entrancy, thread and process models. Microcontroller and DSP architectures, I/O systems, timing and event management. Real-time kernels and services. Techniques for development, debugging and verification. Techniques for limited resource environments. Networking for distributed systems.    ', '(ecse 322)&&(ecse 323)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(60, 'ECSE', '422', '\n      Electrical Engineering : Introduction to fault-tolerant systems. Fault-tolerance techniques through hardware, software, information and time redundancy. Failure classification, failure semantics, failure masking. Exception handling: detection, recovery, masking and propagation, termination vs. resumption. Reliable storage, reliable communication. Process groups, synchronous and asynchronous group membership and broadcast services. Automatic redundancy management. Case studies.    ', '(ecse 322)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(61, 'ECSE', '424', '\n      Electrical Engineering : The course highlights human-computer interaction strategies from an engineering perspective. Topics include user interfaces, novel paradigms in human-computer interaction, affordances, ecological interface design, ubiquitous computing and computer-supported cooperative work. Attention will be paid to issues of safety, usability, and performance.    ', '(ecse 322||comp 251)&&(comp 273)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(62, 'ECSE', '425', '\n      Electrical Engineering : Trends in technology. CISC vs. RISC architectures. Pipelining. Instruction level parallelism. Data and Control Hazards. Static prediction. Exceptions. Dependencies. Loop level paralleism. Dynamic scheduling, branch prediction. Branch target buffers. Superscalar and N-issue machines. VLIW. ILP techniques. Cache analysis and design. Interleaved and virtual memory. TLB translations and caches.    ', '(ecse 322)&&(ecse 323)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(63, 'ECSE', '426', '\n      Electrical Engineering : Introduction to current microprocessors, their architecture, programming, interfacing and operating systems. The course includes lectures, use of crossassemblers, and simulators as well as laboratory experiments on actual microprocessor hardware.    ', '(ccom 206||edec 206)&&(ecse 323)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(64, 'ECSE', '427', '\n      Electrical Engineering : Operating system services, file system organization, disk and cpu scheduling, virtual memory management, concurrent processing and distributed systems, protection and security. Aspects of the DOS and UNIX operating systems and the C programming language. Programs that communicate between workstations across a network.    ', '(ecse 322||comp 273)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(65, 'ECSE', '428', '\n      Electrical Engineering : Software engineering practice in industry, related to the design and commissioning of large software systems. Ethical, social, economic, safety and legal issues. Metrics, project management, costing, marketing, control, standards, CASE tools and bugs. The course involves a large team project.    ', '(ecse 321||comp 335)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(66, 'ECSE', '429', '\n      Electrical Engineering : Correct and complete implementation of software requirements. Verification and validation lifecycle. Requirements analysis, model based analysis, and design analysis. Unit and system testing, performance, risk management, software reuse. Ubiquitous computing.    ', '(ecse 321||comp 303)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(67, 'ECSE', '444', '\n      Electrical Engineering : Design techniques for developing modern microprocessor-based systems, multiple state-of-art instructions set architectures (ISAs) and associated assembly languages, use of tools for compiling, linking, memory overlay; debug techniques for start-stop and real-time debugging, together with debug infrastructure and interfaces: flash patching, variable watching and instruction stream tracing. Use of coprocessors and computer peripherals, such as SPI, I2C, I2S, SAI, USB, wireless standards, timers, DMA units and FLASH accelerators. Interfacing and processing sensor data including multi-sensor integration. Design techniques that promote structured approaches for separation of concerns in computing and communication. Real-time systems and software engineering for tightly integrated hardware.    ', '(ecse 324)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(68, 'ECSE', '456', '      Electrical Engineering : A design project undertaken with close mentorship by a staff member and under the supervision of the course instructor. The project consists of defining an engineering problem, reviewing relevant background, acquiring/analyzing data, and seeking solutions using appropriate simulation/analysis tools and experimental investigations. Professional engineering practices will be followed.    ', '(ecse 211)&&(ecse 322)&&(ecse 323)&&(ecse 330||ccom 206)&&(comp 302)&&(ecse 306)&&(ecse 321)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(69, 'ECSE', '457', '\n      Electrical Engineering : A design project undertaken with close mentorship by a staff member and under the supervision of the course instructor. The course is a continuation of ECSE 456.    ', '(ecse 456)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(70, 'ECSE', '504', '\n      Electrical Engineering : Sampling and aliasing. Conversion of continuous-time controllers using s-to-z transformations; pre-and post-filtering. Discrete time state representation and z-transfer function of sampled linear, time-invariant systems. Correspondence between system theoretic results for continuous- and discrete-time systems. Sampled-data design, including pole placement, LQR control and model predictive control.    ', '(ecse 304||ecse 306)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(71, 'ECSE', '507', '\n      Electrical Engineering : General introduction to optimization methods including steepest descent, conjugate gradient, Newton algorithms. Generalized matrix inverses and the least squared error problem. Introduction to constrained optimality; convexity and duality; interior point methods. Introduction to dynamic optimization; existence theory, relaxed controls, the Pontryagin Maximum Principle. Sufficiency of the Maximum Principle.    ', '(math 264||math 248)&&(math 270||math 271||math 247)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(72, 'ECSE', '523', '\n      Electrical Engineering : Articulatory and acoustic descriptions of speech production, speech production models, speech perception, digital processing of speech signals, vocodors using formant, linear predictive and cepstral techniques, overview of automatic speech recognition systems, speech synthesis systems and speaker verification systems.    ', '(ecse 412||ecse 512)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(73, 'ECSE', '529', '\n      Electrical Engineering : Vision in man and machine, imaging process, spatial and frequency domain filters, biological vision, edge detection, intermediate features, connecting biological and psychophysical vision, science of colour.    ', '(ecse 304||ecse 306)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(74, 'ECSE', '530', '\n      Electrical Engineering : The place of logic synthesis in microelectronics. Representations of Boolean functions: logic covers, binary decision diagrams. Two-level synthesis algorithms, Espresso. Multi-level synthesis to Boolean networks: don\'t care methods, algebraic optimizations, delay modelling. Sequential synthesis: state-based optimizations, state assignment, network optimizations. Technology mapping: library cell and FPGA mapping.    ', '(ecse 323)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(75, 'ECSE', '532', '\n      Electrical Engineering : Introduction to computer graphics systems and display devices: raster scan, scan conversion, graphical input and interactive techniques - window environments; display files: graphics languages and data structures: 2D transformations; 3D computer graphics, hidden line removal and shading; graphics system design; applications. Laboratory project involving the preparation and running of graphics programs.    ', '(ecse 322)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(76, 'ECSE', '539', '\n      Electrical Engineering : Practical and theoretical knowledge for developing software languages and models; foundations for model-based software development; topics include principles of model-driven engineering; concern-driven development; intentional, structural, and behavioral models as well as configuration models; constraints; language engineering; domain-specific languages; metamodelling; model transformations; models of computation; model analyses; and modeling tools.    ', '(comp 303||ecse 321)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(77, 'ECSE', '570', '\n      Electrical Engineering : Acoustic phonetics and signal representations. Pattern classification, stochastic modelling, language modelling and search algorithms as applied to speech recognition. Techniques for robustness, integration of speech recognition with other user interface modalities, and the role of automatic speech recognition in speech understanding.    ', '(ecse 305)&&(ecse 322)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(78, 'ENVR', '201', '\n      Environment : This course deals with how scientific-technological, socio-economic, political-institutional and behavioural factors mediate society-environment interactions. Issues discussed include population and resources; consumption, impacts and institutions; integrating environmental values in societal decision-making; and the challenges associated with, and strategies for, promoting sustainability. Case studies in various sectors and contexts are used.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(79, 'ENVR', '203', '\n      Environment : Introduction to cultural perspectives on the environment: the influence of culture and cognition on perceptions of the natural world; conflicts in orders of knowledge (models, taxonomies, paradigms, theories, cosmologies), ethics (moral values, frameworks, dilemmas), and law (formal and customary, rights and obligations) regarding political dimensions of critical environments, resource use, and technologies.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(80, 'ENVR', '400', '\n      Environment : Students work in interdisciplinary seminar groups on challenging philosophical, ethical, scientific and practical issues. They will explore cutting-edge ideas and grapple with the reconciliation of environmental imperatives and social, political and economic pragmatics. Activities include meeting practitioners, attending guest lectures, following directed readings, and organizing, leading and participating in seminars.    ', '(envr 203)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(81, 'FACC', '100', '\n      Faculty Course : Introduction to engineering practice; rights and code of conduct for students; professional conduct and ethics; engineer\'s duty to society and the environment; sustainable development; occupational health and safety; overview of the engineering disciplines taught at McGill.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(82, 'FACC', '220', '\n      Faculty Course : Aspects of the law which affect architects and engineers. Definition and branches of law; Federal and Provincial jurisdiction, civil and criminal law and civil and common law; relevance of statutes; partnerships and companies; agreements; types of property, rights of ownership; successions and wills; expropriation; responsibility for negligence; servitudes/easements, privileges/liens, hypothecs/ mortgages; statutes of limitations; strict liability of architect, engineer and builder; patents, trade marks, industrial design and copyright; bankruptcy; labour law; general and expert evidence; court procedure and arbitration.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(83, 'FACC', '300', '\n      Faculty Course : Introduction to the basic concepts required for the economic assessment of engineering projects. Topics include: accounting methods, marginal analysis, cash flow and time value of money, taxation and depreciation, discounted cash flow analysis techniques, cost of capital, inflation, sensitivity and risk analysis, analysis of R and D, ongoing as well as new investment opportunities.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(84, 'FACC', '400', '      Faculty Course : Laws, regulations and codes governing engineering professional practice. Responsibility and liability. Environmental legislation. Project and organization management. Relations between engineer and client. Technical practice - analysis, design, execution and operation.    ', '(facc 100||bree 205)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(85, 'FACC', '500', '\n      Faculty Course : This course combines several management functional areas such as marketing, financial, operations and strategy with the skills of creativity, engineering innovation, leadership and communications. Students learn how to design an effective and winning business plan around a technology or engineering project in small, medium or large enterprises.    ', '(facc 300||mime 310)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(86, 'FACC', '501', '\n      Faculty Course : Students work in teams to develop a comprehensive business plan project based on a technological or engineering innovation while utilizing site visits.    ', '(facc 500)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(87, 'GEOG', '200', '\n      Geography : Introduction to geography as the study of nature and human beings in a spatial context. An integrated approach to environmental systems and the human organization of them from the viewpoint of spatial relationships and processes. Special attention to environmental problems as a constraint upon Third World development.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(88, 'GEOG', '203', '\n      Geography : An introduction to system-level interactions among climate, hydrology, soils and vegetation at the scale of drainage basins, including the study of the global geographical variability in these land-surface systems. The knowledge acquired is used to study the impact on the environment of various human activities such as deforestation and urbanisation.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(89, 'GEOG', '205', '\n      Geography : An examination of global change, from the Quaternary Period to the present day involving changes in the physical geography of specific areas. Issues such as climatic change and land degradation will be discussed, with speculations on future environments.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(90, 'GEOG', '302', '      Geography : An ecological analysis of the physical and biotic components of natural resource systems. Emphasis on scientific, technological and institutional aspects of environmental management. Study of the use of biological resources and of the impact of individual processes.    ', '(biol 308)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(91, 'INDR', '294', '\n      Industrial Relations : An introduction to labour-management relations, the structure, function and government of labour unions, labour legislation, the collective bargaining process, and the public interest in industrial relations.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(92, 'MATH', '133', '\n      Mathematics & Statistics (Sci) : Systems of linear equations, matrices, inverses, determinants; geometric vectors in three dimensions, dot product, cross product, lines and planes; introduction to vector spaces, linear dependence and independence, bases; quadratic loci in two and three dimensions.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(93, 'MATH', '140', '\n      Mathematics & Statistics (Sci) : Review of functions and graphs. Limits, continuity, derivative. Differentiation of elementary functions. Antidifferentiation. Applications.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(94, 'MATH', '141', '\n      Mathematics & Statistics (Sci) : The definite integral. Techniques of integration. Applications. Introduction to sequences and series.    ', '(math 139||math 140||math 150)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(95, 'MATH', '262', '\n      Mathematics & Statistics (Sci) : Series and power series, including Taylor\'s theorem. Brief review of vector geometry. Vector functions and curves. Partial differentiation and differential calculus for vector valued functions. Unconstrained and constrained extremal problems. Multiple integrals including surface area and change of variables.    ', '(math 141)&&(math 133)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(96, 'MATH', '263', '\n      Mathematics & Statistics (Sci) : First order ODEs. Second and higher order linear ODEs. Series solutions at ordinary and regular singular points. Laplace transforms. Linear systems of differential equations with a short review of linear algebra.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(97, 'MATH', '264', '\n      Mathematics & Statistics (Sci) : Review of multiple integrals. Differential and integral calculus of vector fields including the theorems of Gauss, Green, and Stokes. Introduction to partial differential equations, separation of variables, Sturm-Liouville problems, and Fourier series.    ', '(math 262||math 151||math 152)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(98, 'MATH', '270', '\n      Mathematics & Statistics (Sci) : Introduction. Review of basic linear algebra. Vector spaces. Eigenvalues and eigenvectors of matrices. Linear operators.    ', '(math 263)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(99, 'MATH', '338', '\n      Mathematics & Statistics (Sci) : Egyptian, Babylonian, Greek, Indian and Arab contributions to mathematics are studied together with some modern developments they give rise to, for example, the problem of trisecting the angle. European mathematics from the Renaissance to the 18th century is discussed in some detail.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(100, 'MATH', '363', '\n      Mathematics & Statistics (Sci) : Logic and combinatorics. Mathematical reasoning and methods of proof. Sets, relations, functions, partially ordered sets, lattices, Boolean algebra. Propositional and predicate calculi. Recurrences and graph theory.    ', '(math 263)&&(math 264)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(101, 'MECH', '526', '\n      Mechanical Engineering : Course topics include: clean manufacturing, product and process design for minimizing materials and energy use, the product life cycle, impact of technology on the environment, environmental impact assessment, regulatory process, and managing the "political" process.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(102, 'MGCR', '222', '\n      Management Core : Individual motivation and communication style; group dynamics as related to problem solving and decision making, leadership style, work structuring and the larger environment. Interdependence of individual, group and organization task and structure.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(103, 'MGCR', '352', '\n      Management Core : Introduction to marketing principles, focusing on problem solving and decision making. Topics include: the marketing concept; marketing strategies; buyer behaviour; Canadian demographics; internal and external constraints; product; promotion; distribution; price. Lectures, text material and case studies.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(104, 'MGPO', '440', '\n      Management Policy : This course explores the relationship between economic activity, management, and the natural environment. Using readings, discussions and cases, the course will explore the challenges that the goal of sustainable development poses for our existing notions of economic goals, production and consumption practices and the management of organizations.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(105, 'MIME', '308', '\n      Mining & Materials Engineering : Critical examination of the socio-economic costs and benefits of technology, case studies of old engineering works and new technologies. The integration of applied ethics and engineering practice, analysis of basic concepts of technology assessment, the inter-connected processes of risk assessment, management, and communication.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(106, 'ORGB', '321', '      Organizational Behaviour : Leadership theories provide students with opportunities to assess and work on improving their\nleadership skills. Topics include: the ability to know oneself as a leader, to formulate a vision, to have the courage to lead, to lead creatively, and to lead effectively with others.    ', '(mgcr 222)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(107, 'ORGB', '423', '\n      Organizational Behaviour : Issues involved in personnel administration. Topics include: human resource planning, job analysis, recruitment and selection, training and development, performance appraisal, organization development and change, issues in compensation and benefits, and labour-management relations.    ', '(mgcr 222)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(108, 'PHIL', '343', '\n      Philosophy : An investigation of ethical issues as they arise in the practice of medicine (informed consent, e.g.) or in the application of medical technology (in vitro fertilization, euthanasia, e.g.)    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(109, 'PHYS', '131', '\n      Physics : The basic laws and principles of Newtonian mechanics; oscillations, waves, and wave optics.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(110, 'PHYS', '142', '\n      Physics : The basic laws of electricity and magnetism; geometrical optics.    ', '(phys 131)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(111, 'RELG', '270', '\n      Religious Studies : Environmental potential of various religious traditions and secular perspectives, including animal rights, ecofeminism, and deep ecology.\n\n    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(112, 'SOCI', '235', '\n      Sociology (Arts) : An examination of the extent to which technological developments impose constraints on ways of arranging social relationships in bureaucratic organizations and in the wider society: the compatibility of current social structures with the effective utilization of technology.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(113, 'SOCI', '312', '\n      Sociology (Arts) : The development of the world of work from the rise of industrial capitalism to the postindustrial age. Responses of workers and managers to changing organizational, technological and economic realities. Interrelations between changing demands in the workplace and the functioning of the labour market. Canadian materials in comparative perspective.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(114, 'URBP', '201', '\n      Urban Planning : The study of how urban planners respond to the challenges posed by contemporary cities world-wide. Urban problems related to the environment, shelter, transport, human health, livelihoods and governance are addressed; innovative plans to improve cities and city life are analyzed.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(115, 'ARCH', '201', '\n      Architecture : Introduction to design; development of design judgement and communication skills in a series of exercises addressing light, scale, space, form and colour in the built environment; introduction to techniques of oral and graphic presentation, including model making, photography, sketching and architectural drawing. The course is based in the studio and includes lectures, seminars and field trips.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(116, 'ARCH', '202', '\n      Architecture : Introduction to architectural design; consideration of building form in relation to program, structural system, material selection, site and climate; further development of skills in model making, conventional architectural drawing, axonometric and perspective drawing, sketching and architectural rendering. The course is based in the studio and includes lectures, seminars and field trips.    ', '(arch 201)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(117, 'ARCH', '221', '\n      Architecture : Strategies for visualization and representation based on perspective, orthographic and oblique projection; drawing in the design process; relationship of drawing type to design intention; freehand drawing and sketching; architectural survey and notebook recording. Students work in the studio and in the field in a range of media.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(118, 'ARCH', '240', '\n      Architecture : The characteristics of basic building materials: wood, steel, masonry and concrete. How building materials are shaped into building components, and how these components are integrated into the building envelope. Problems, laboratory projects and field trips to illustrate principles.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(119, 'ARCH', '241', '\n      Architecture : Introduction to the basic concepts and forms of structures in architecture.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(120, 'ARCH', '250', '\n      Architecture : The study of architecture and cities from ancient times to 1750.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(121, 'ARCH', '251', '\n      Architecture : Global architectural urban history in the 20th century.  The course is organized chronologically. The topic of each week is coupled with focus on an individual city, and introduces the work of an architect.    ', '(arch 250)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(122, 'ARCH', '303', '\n      Architecture : An exploration of the design of buildings. Projects emphasize the major social, technological, environmental, and symbolic aspects of the design process. Introduction to specific modelling, presentation, and documentation techniques. Discussions, readings, field trips and practical exercises.    ', '(arch 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(123, 'ARCH', '304', '\n      Architecture : Continuation of Design and Construction I with projects of increasing complexity. Projects deal with particular aspects of architectural design and/or explore approaches to design methodology. Discussions, readings, field trips and practical exercises.    ', '(arch 303)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(124, 'ARCH', '325', '\n      Architecture : Eight days of supervised field sketching outside Montreal in the late summer, followed by coursework and fieldwork in Montreal examining architectural sketching as a process that develops an intellectual and physical framework for encounters with the urban environment. Students work mainly in the field, in selected media.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(125, 'ARCH', '342', '\n      Architecture : This course introduces students to digital representation in architecture. Students explore applications of state-of-the-art two- and three-dimensional computer modeling software in architectural design.    ', '(arch 201)&&(arch 221)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(126, 'ARCH', '354', '\n      Architecture : The study of Modern European architecture from 1750 to 1950.    ', '(arch 250)&&(arch 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(127, 'ARCH', '355', '\n      Architecture : The study of cities and world architecture from 1900 to the present.    ', '(arch 250)&&(arch 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(128, 'ARCH', '375', '\n      Architecture : Land form, plant life, microclimate; land use and land preservation; elements and methods of landscape design.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(129, 'ARCH', '377', '\n      Architecture : Exploration of the interrelationship between energy, environment and building. Topics include sustainability, assessment tools, the integrated design process, water conservation, energy conservation, renewable energy, materials and embodied energy, indoor environmental quality, environmental acoustics, and advanced building technology.    ', '(arch 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(130, 'ARCH', '378', '\n      Architecture : The study of the creation, form and usage of the exterior space generated in various patterns of low-rise housing. Socio-cultural aspects of patterns; exterior space as a logical extension of the living unit; social control of the use of urban and suburban land; comparative model for low-rise housing patterns.    ', '(arch 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(131, 'ARCH', '379', '\n      Architecture : Studies in-situ of key buildings, landscapes and urban settings; techniques of graphic documentations, analysis of physical configuration, constructional details and present use. Excursions to neighbouring sites of architectural interest.    ', '(arch 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(132, 'ARCH', '383', '\n      Architecture : Geometry in the formal structure of design. Grids, lattices, polygons and polyhedra; proportional systems. Evidence of these figures and structures in natural objects and phenomena. Graphical and physical models. Application to architecture and the human environment. Case studies.    ', '(arch 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(133, 'ARCH', '405', '\n      Architecture : A structured investigation of architectural concepts; program interpretation with respect to relevant cultural, social and environmental contexts; applications of appropriate formal languages and building technologies in integrated proposals for a variety of building forms.    ', '(arch 304)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(134, 'ARCH', '406', '\n      Architecture : A detailed study and comprehensive development of architectural proposals for complex building types and site conditions; the exploration of coherent initial concepts with respect to programmatic requirements, image and form; subsequent elaboration leading to meaningful and technologically viable designs for the built environment.    ', '(arch 405)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(135, 'ARCH', '447', '\n      Architecture : Concepts of natural and artificial lighting in architecture and urban design.    ', '(arch 304)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(136, 'ARCH', '451', '\n      Architecture : The study of building codes with specific emphasis on the National Building and National Fire Codes of Canada. Examples of existing buildings with assignments to illustrate regulations. Development of a systematic approach to the implementation of codes during the preliminary design stage of an architectural project.    ', '(arch 405)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(137, 'ARCH', '461', '\n      Architecture : Drawing and sketching in pencil, charcoal and other media both in the studio and out-of-doors.    ', '(arch 324)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(138, 'ARCH', '490', '\n      Architecture : A course to allow the introduction of special topics in related areas of design.    ', '(arch 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(139, 'ARCH', '512', '\n      Architecture : Architectural modelling using advanced applications in digital media. Topics include: 3-D modelling and rendering; image editing; digital animation; hypertext and the World Wide Web; issues of representation and methodology; comparison of publishing applications. Projects complement design studio courses and independent studies that are student or instructor initiated.    ', '(arch 304)&&(arch 342)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(140, 'ARCH', '514', '\n      Architecture : Context-focused community-based architectural projects undertaken in collaboration with external partners. Exploration of challenges, needs, and opportunities in architectural and urban-design interventions on real sites through interdisciplinary and cross-sectorial collaboration.    ', '(arch 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(141, 'ARCH', '515', '\n      Architecture : This course will address sustainable design theory and applications in the built environment with students from a variety of fields (architecture, urban planning, engineering, sociology, environmental studies, economics, international studies). Architecture will provide the focus for environmental, socio-cultural and economic issues.    ', '(arch 377)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(142, 'ARCH', '517', '\n      Architecture : Design strategies of sustainable residential environments at the community and the unit levels. Historic references, siting principles, high density, healthy developments, green homes, urban renewal, circulation and parking, open spaces and implementation approaches.    ', '(arch 377)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(143, 'ARCH', '520', '\n      Architecture : Historical, geographical, demographical, and regional evolution of the metropolis of Montreal. Topics include: important quartiers, the Montreal urban grid, industrialization, reform movements, geographical diversity, urban culture, local building techniques and materials. Basic concepts of urban morphology and their relationships to the contemporary urban context will be explored.    ', '(arch 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(144, 'ARCH', '521', '\n      Architecture : Nature, pattern and life of modern cities. Urban networks, special areas, problems and prospects.    ', '(arch 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(145, 'ARCH', '523', '\n      Architecture : Critical study of significant architectural thought since 1750 as it has been expressed in buildings and texts (treatises, manifestos, criticisms). A specific theme will be addressed every year to allow in-depth interpretations of the material presented and discussed.    ', '(arch 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(146, 'ARCH', '525', '\n      Architecture : Analysis and evaluation of significant architectural projects with reference to contemporary architectural theories.    ', '(arch 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(147, 'ARCH', '526', '\n      Architecture : Philosophy of Structure aims to investigate structure in its broadest sense. The course is divided in two halves; the first one gives an overview of the development of theoretical structural frameworks such as mathematics and geometry, while the second one highlights physical structures constructed by nature (geology, turbulence), man or animals.    ', '(arch 202)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(148, 'ARCH', '527', '\n      Architecture : The elements of form in buildings and their siting design in the urban setting.    ', '(arch 378)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(149, 'ARCH', '529', '\n      Architecture : A review of environmental alternatives in housing; contemporary housing and the physical and sociological determinants that shape it; Canadian housing.    ', '(arch 528)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(150, 'ARCH', '531', '\n      Architecture : Architectural intentions embodied in buildings and writings of architects from antiquity to the Renaissance. Special emphasis is placed on the cultural connections of architecture to science and philosophy.    ', '(arch 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(151, 'ARCH', '532', '\n      Architecture : Examination of architectural intentions (theory and practice) in the European context (especially France, Italy and England), during the crucial period that marks the beginning of the modern era.    ', '(arch 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(152, 'ARCH', '533', '\n      Architecture : An exploration of the aims, tools, and methods of Architectural History as a discipline; the use of primary sources from the Canadian Centre for Architecture and other archives.    ', '(arch 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(153, 'ARCH', '535', '\n      Architecture : French, British and American influences in the history of Canadian architecture, with particular emphasis on the Eastern Provinces. Site visits and case studies.    ', '(arch 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(154, 'ARCH', '536', '\n      Architecture : Historic attitudes and terminologies of conservation; historic research techniques. Restoration technology of building materials and principles of interior design in the 19th and 20th century; current preservation planning.    ', '(arch 251)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(155, 'ARCH', '540', '\n      Architecture : A course to allow the introduction of new topics in Architecture as needs arise, by regular and visiting staff.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(156, 'ARCH', '541', '\n      Architecture : A course to allow the introduction of new topics in Architecture as needs arise, by regular and visiting staff.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(157, 'ARCH', '564', '\n      Architecture : Designing for sustainable development to meet the Millennium and its new environmental goals. Approaches, strategies and projects that meet these goals in areas of economic empowerment, food security, gender equity, health and sanitation, and shelter sectors.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(158, 'ARCH', '566', '\n      Architecture : Overview of cultural landscapes studies, methodologies, and resources. Comparative studies of the connection between people, place, and artifact systems through a critical examination of architecture, regional context, and material culture. Examination of precedents for the interpretation of cultural landscapes by architects, ethnologists, anthropologists, folklorists, historians, writers, filmmakers, photographers, and artists.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(159, 'CIVE', '284', '\n      Civil Engineering : Basic principles of statics; force systems; trusses; centroids and second moment of areas; stress and strain; beams; shearing and bending stresses; deflections; combined stresses; columns.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(160, 'CIVE', '385', '\n      Civil Engineering : Structural loadings, load factors, code requirements and design procedures. Characteristics of structural steel and structural timber in building construction. Structural design of axially loaded tension and compression members, joists, beams, girders, trusses and framing systems.    ', '(cive 284)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(161, 'CIVE', '388', '\n      Civil Engineering : Physical properties of concrete; behaviour and design of reinforced concrete members in compression, tension, bending, shear and combined loadings; bond and anchorage; soil properties, soil testing, footings; pile foundation; shorting; retaining walls.    ', '(cive 284)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(162, 'CIVE', '492', '\n      Civil Engineering : A study of structural systems in concrete, steel, timber; a philosophy of structure; choice of structure; economic factors in design; recent developments and trends in structure; lateral stability by frame action, bracing shear walls; mechanics of certain structural forms.    ', '(cive 385)&&(cive 388)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(163, 'OCC1', '442', '\n      Occupational Therapy : Students work in multi-disciplinary teams under the supervision of faculty and visitors on selected problems encountered  in the design and construction of environments for the physical disabled.    ', '(arch 303)&&(occ1 339)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(164, 'CFIN', '410', '\n      Finance (CCE) : This course covers financial instruments such as stocks and bonds, formation and management of investment portfolios and evaluation of portfolio performance. Finance theories and investment practices relating to such additional topics as global portfolio diversification, investments by means of Initial Public Offerings, hedge funds, private equity and derivatives are presented.    ', '(mgcr 211)&&(mgcr 341)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(165, 'CIVE', '205', '\n      Civil Engineering : Systems of forces and couples, resultants, equilibrium. Trusses, frames and beams, reactions, shear forces, bending moments. Centroids, centres of gravity, distributed forces, moments of inertia. Friction, limiting equilibrium, screws, belts.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(166, 'CIVE', '207', '\n      Civil Engineering : Stress-strain relationships; elastic and inelastic behaviour; performance criteria. Elementary and compound stress states, Mohr\'s circle. Shear strains, torsion. Bending and shear stresses in flexural members. Deflections of beams. Statically indeterminate systems under flexural and axial loads. Columns. Dynamic loading.    ', '(cive 205||mech 210)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(167, 'CIVE', '208', '\n      Civil Engineering : Introduction to civil engineering systems; system modelling process; systems approach and optimization techniques; application of linear programming; simplex method; duality theory; sensitivity analysis; transportation problem; assignment problem; network analysis including critical path method; integer linear programming method.    ', '(comp 208)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(168, 'CIVE', '311', '\n      Civil Engineering : The origin and formation of soils. Soil classification systems and phase composition in soils. Stress states in geotechnical materials. Total stresses, pore fluid pressures and effective stresses. TerzaghiÃ¢â‚¬â„¢s concept of effective stresses. Time-dependent influences of pore pressure development during loading. Water flow in porous media. DarcyÃ¢â‚¬â„¢s law. Laboratory and field measurement of hydraulic conductivity. Hydraulic conductivity of soil composites. Deformation of geomaterials. Elastic deformations and consolidation behaviour. Failure of soils. The Mohr-Coulomb criterion. Stress paths. Dilatancy, TaylorÃ¢â‚¬â„¢s interpretation of stress dilatancy and applications.    ', '(cive 207)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(169, 'CIVE', '416', '\n      Civil Engineering : Earth pressure theory, retaining walls, sheet pile walls, braced excavations. Slope stability analysis. 2D flow through isotropic and anisotropic soils. Bearing capacity and settlement of shallow foundations, stress distribution. Deep foundations, single pile, pile groups. Geotechnical investigation and reports.    ', '(cive 311)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(170, 'CIVE', '421', '\n      Civil Engineering : Design of water-related municipal services; sources of water and intake design; estimation of water demand and wastewater production rates; design, construction and maintenance of water distribution, wastewater and stormwater collection systems; pumps and pumping stations; pipe materials, network analysis and optimization; storage; treatment objectives for water and wastewater.    ', '(cive 327)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(171, 'CIVE', '514', '\n      Civil Engineering : Stress, strain, and basic equations of elasticity. Stress function solutions. Stress concentration and failure criteria; elements of linear fracture mechanics. Unsymmetrical bending of beams; shear centres; torsion of thin-walled members. Elementary analysis of plates and their buckling. Membrane stresses in shells.    ', '(cive 207)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(172, 'CIVE', '584', '\n      Civil Engineering : Origins and types of groundwater; Darcy\'s law; hydraulic anisotropy; conservation laws; fundamental equations of porous media flow; Laplace\'s and Poisson\'s equations: analytical solution of potential flow problems; determination of hydraulic conductivity; flow in unconfined and confined acquifers; seepage modelling; unsaturated flow; transient flows in porous media; introduction to computational methods.    ', '(cive 311)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(173, 'COMP', '208', '\n      Computer Science (Sci) : Introduction to computer systems. Concepts and structures for high level programming. Elements of structured programming using FORTRAN 90 and C. Numerical algorithms such as root finding, numerical integration and differential equations. Non-numerical algorithms for sorting and searching.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(174, 'ECSE', '461', '\n      Electrical Engineering : Electric and magnetic circuits. Notions of electromechanical energy conversion applied to electrical machines. Basic electrical machines - transformers, direct-current motors, synchronous motors and generators, three phase and single phase induction machines. Elements of modern electronically controlled electric drive systems.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(175, 'EPSC', '221', '\n      Earth & Planetary Sciences : An introductory course in physical geology designed for majors in civil and mining engineering. Properties of rocks and minerals, major geological processes, together with natural hazards and their effects on engineered structures are emphasized. The laboratory is an integral part of the course which includes rock and mineral identification, basic techniques of airphoto and geological map interpretation, and structural geology.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(176, 'EPSC', '225', '\n      Earth & Planetary Sciences : Survey of the physical and chemical properties of the main mineral groups. Discussion of their relationships to the chemical composition and structure of minerals. The practical exercises emphasize the physical and chemical properties that relate to industrial uses and environmental issues, and the identification of hand specimens.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(177, 'EPSC', '320', '      Earth & Planetary Sciences : Physical properties of Earth and the processes associated with its existence as inferred from astronomy, geodesy, seismology, geology, terrestrial magnetism and thermal evolution.    ', '(math 133)&&(math 222)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(178, 'EPSC', '549', '\n      Earth & Planetary Sciences : Introduction to groundwater flow through porous media. Notions of fluid potential and hydraulic head. Darcy flux and Darcy\'s Law. Physical properties of porous media and their measurement. Equation of groundwater flow. Flow systems. Hydraulics of pumping and recharging wells. Notions of hydrology. Groundwater quality and contamination. Physical processes of contaminant transport.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(179, 'FINE', '482', '\n      Finance : The international financial environment as it affects the multinational manager. Balance of payments concepts, adjustment process of the external imbalances and the international monetary system. In depth study of the institutional and theoretical aspects of foreign exchange markets; international capital markets, including Eurobonds and eurocredit markets.    ', '(mgcr 341)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(180, 'MECH', '289', '\n      Mechanical Engineering : The design process, including free-hand sketching; from geometry construction to engineering construction; the technology and standards of engineering graphic communication; designing with CAD software. The role of visualization in the production of engineering designs.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(181, 'MIME', '200', '\n      Mining & Materials Engineering : Economic importance of the minerals industry. Mining: legislation, regulations, criteria for exploiting an ore: mining methods, equipment. Extractive metallurgy: mineral processing, hydrometallurgy, pyrometallurgy. Environmental protection.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(182, 'MIME', '203', '\n      Mining & Materials Engineering : Introduction to surveying. Definitions and mathematics. Measurement of levels, angles and distances. Fundamentals of control surveying. Underground mine surveying. GPS and laser applications.    ', '(mech 289)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(183, 'MIME', '209', '\n      Mining & Materials Engineering : Introduction to stochastic modelling of mining and metallurgical engineering processes. Description and analysis of data distributions observed in mineral engineering applications. Modelling with linear regression analysis. Taylor series application to error and uncertainty propagation. Metallurgical mass balance adjustments.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(184, 'MIME', '260', '\n      Mining & Materials Engineering : Structure properties and fabrication of metals, polymers, ceramics, composites; engineering properties: tensile, fracture, creep, oxidation, corrosion, friction, wear; fabrication and joining methods; principles of materials selection.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(185, 'MIME', '290', '\n      Mining & Materials Engineering : A four-month work period in the mineral industry, to expose the student to an industrial environment. Candidates will receive basic industrial training. A complete report must be submitted at the end of the term.    ', '(mime 200)&&(mime 203)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(186, 'MIME', '291', '\n      Mining & Materials Engineering : A four-month industrial work period in a mining company, research laboratory or government agency. The student will receive formal industrial training in a technical position. A complete report must be submitted at the end of the term.    ', '(mime 290)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(187, 'MIME', '320', '\n      Mining & Materials Engineering : The extraction of energy resources, i.e. coal, gas, oil and tar sands. After a brief geological review, different extraction techniques for these substances will be discussed. Emphasis on problems such as northern mining and offshore oil extraction with reference to Canadian operations. Transportation and marketing.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(188, 'MIME', '322', '\n      Mining & Materials Engineering : Principles of drilling, penetration rates, performance and factors to consider in the choice of a drilling method. Characteristics of explosives, firing systems and blast patterns. Blasting techniques in surface and underground workings and in permafrost. Special blasting techniques at excavation perimeters. Vibration and noise control. Economics of drill/blast practice, interface with transport and crushing systems. Legislation and safety in explosives use and handling. Ripping and fullface boring machines.    ', '(mime 200)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(189, 'MIME', '323', '\n      Mining & Materials Engineering : Characteristics of soil and rock masses and the stability of mine workings. Mechanical properties of rocks and soils related to physical/chemical properties. Characterization of rock mass discontinuities. Laboratory and in-situ techniques to define mechanical properties of soils, rocks and discontinuities. Permeability and groundwater flow principles. In-situ stresses and their measurement. Rock mass quality and classification systems.    ', '(epsc 221)&&(mime 200)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(190, 'MIME', '325', '\n      Mining & Materials Engineering : Geographical distribution of mineral resources. Production, consumption and prices of minerals. Market structure of selected minerals. Economic evaluation aspects: grade-tonnage considerations; capital and operating cost estimation; assessment of market conditions; estimation of revenue; taxation; sensitivity and risk analyses; economic optimization of mine development and extraction.    ', '(facc 300||mime 310)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(191, 'MIME', '329', '\n      Mining & Materials Engineering : Methods of field mapping of mineral deposits, drilling and sampling; concept of cut-off grade; reserves estimation by conventional methods; reserves evaluation by geostatistical methods.    ', '(epsc 221)&&(mime 200)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(192, 'MIME', '333', '\n      Mining & Materials Engineering : Physical and mechanical characteristics of materials related to loading, transport and storage. Dynamics of particles, systems and rigid bodies, mass-acceleration, work-energy, impulse-momentum. Types and selection of excavation and haulage equipment. Layout of haul roads. Rail transport. Conveyor belts and chain conveyors. Mine hoists. Layout of mine shafts.    ', '(mime 200)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(193, 'MIME', '340', '\n      Mining & Materials Engineering : Flow analysis and manometry. Conservation of mass and momentum. Flow in pipes and ducts, analysis of pipe networks. First and second law of thermodynamics and their applications. Open channel flows. Dimensional analysis and similitude. Flow measurements. Settling and separation of particles. Non-Newtonian flow and slurry transport. Fluidized beds. Filtration of liquid/solid mixtures.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(194, 'MIME', '341', '\n      Mining & Materials Engineering : Theory and practice of unit operations including: size reduction-crushing and grinding; size separation-screening and classification; mineral separation-flotation, magnetic and gravity separation. Equipment and circuit design and selection. Mass balancing. Laboratory procedures: grindability, liberation, magnetic and gravity separation, flotation and solid-liquid separation.    ', '(mime 200||mime 250)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(195, 'MIME', '392', '\n      Mining & Materials Engineering : A four-month industrial work period in a mining company, research laboratory or government agency. Based on the experience gained during the first two work periods, the student may be asked to undertake more challenging technical tasks. A complete report must be submitted at the end of the term.    ', '(mime 291)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(196, 'MIME', '413', '\n      Mining & Materials Engineering : Strategic decision-making for mine planning given uncertain metal supply from orebodies and commodity demand. Stochastic optimization techniques in mine design and production scheduling. Uncertainty quantification and orebody representation. Stochastic mine optimization in applications from open pit and underground metal mines. Introduction to sustainable development and utilization of mineral resources.    ', '(mime 325)&&(mime 419)&&(mpmc 326)&&(mpmc 329)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(197, 'MIME', '419', '\n      Mining & Materials Engineering : Choice of a surface mining method. Analysis of soil and rock mass properties related to surface mining. Calculation and monitoring of stripping ratios, ultimate pit depth, slope stability, rock reinforcement, bench and berm dimensioning and ramp design. Loading and hauling systems. Surface layout and development. Water drainage systems. Production and cost analysis. Computerized design techniques.    ', '(mime 322)&&(mime 333)&&(mime 325)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(198, 'MIME', '421', '\n      Mining & Materials Engineering : Stresses around excavations: analytical and numerical solutions. Stability of underground and surface excavations: structural instability analysis with stereonet, instability due to high stress. Rock supports. Monitoring. Case studies.    ', '(mime 323)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(199, 'MIME', '422', '\n      Mining & Materials Engineering : Statutory regulations and engineering design criteria. Occupational health hazards of mine gases, dusts, etc. Ventilation system design. Natural and mechanical ventilation. Measuring and modelling air flow in ventilation networks. Calculation of head losses. Selection of mine ventilation fans. Air heating and cooling. Aspects of economics.    ', '(mime 340)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(200, 'MIME', '424', '\n      Mining & Materials Engineering : Fundamentals of underground mining methods; orebody characteristics and host rock; drilling and blasting procedures; general and specialized equipment; mining method selection criteria; cost estimation; calculation of cut-off grade, ore recovery and dilution; mine developments and underground infrastructure: raises, chutes, loading pockets, dump points, raises, and ore bins; mine safety practice.    ', '(mime 322)&&(mime 325)&&(mime 333)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(201, 'MIME', '425', '\n      Mining & Materials Engineering : Representing uncertainty in orebody models and shortcomings of conventional methods including reserve reporting and mine planning. Stochastic spatial sequential simulation methods. Joint simulation of multiple correlated elements. Simulation material types. Introduction to geometallurgical modelling. Additional drilling, reserve classification, grade control and mine planning optimization. Risk quantification in life-of-mine production schedules.    ', '(mpmc 326)&&(mpmc 329)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(202, 'MIME', '426', '\n      Mining & Materials Engineering : A case study design exercise. Design and selection of the facilities required to start production. Use of design criteria dictated by mining plans, geography, geology and government regulations. Professional appraisal of the technical and economic prefeasibility of developing a mineral deposit. Students prepare a professional-level report and seminar presentation.    ', '(mime 333)&&(mime 325)&&(mime 421||mpmc 321)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(203, 'MIME', '442', '\n      Mining & Materials Engineering : Tools and methods of process analysis, modelling and optimization using flotation and comminution examples: sampling theory and statistics, data reconciliation, statistical experimental design. Kinetic models of flotation and comminution; simulation software. Residence time distributions: tanks-in-series and axial dispersion models. Combined flotation/comminution models. Introduction to geostatistics and data mining.    ', '(mime 341)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(204, 'MIME', '484', '      Mining & Materials Engineering : A mining research project to be completed during one semester. The project must be approved by an academic advisor. A comprehensive report and a seminar presentation are required for the project.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(205, 'MIME', '494', '\n      Mining & Materials Engineering : A four-month industrial work period after which the student must submit a report.    ', '(mime 419)&&(mpmc 328)&&(mpmc 421)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(206, 'MIME', '520', '\n      Mining & Materials Engineering : The properties of rock masses and of structural discontinuities. Influence of geological structure on stability. Linear, non-linear, and wedge failures. Site investigations. Methods of slope stabilization.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(207, 'MIME', '527', '      Mining & Materials Engineering : A comprehensive study of selected topics in the mineral resource sector.    ', '', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(208, 'MIME', '544', '\n      Mining & Materials Engineering : The course covers three main topics: principles of separation, including data presentation, properties of recovery/ yield plots, technical and economic efficiency and identification of limits to separation; column flotation, hydrodynamics of collection and froth zones, mixing, scale-up and design, measurements and control; surface and electrochemistry, including absorption, surface charge, coagulation, electron transfer reactions, electrochemistry in plant practice.    ', '(mime 341)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(209, 'MIME', '545', '\n      Mining & Materials Engineering : Gold recovery (as a Professional Development Seminar): methods of recovery (gravity, flotation, cyanidation), refractory gold (roasting, pressure oxidation, bacterial leaching), dissolved gold recovery (Merrill-Crowe) and activated carbon methods. Sampling: definition of errors, sample extraction, size, and processing. Mass balancing: basic considerations, definition of networks, software. Blending: auto-correlation functions, transfer functions, blending systems. Effect of feed variability.    ', '(mime 341)', NULL, NULL);
INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(210, 'MIME', '588', '\n      Mining & Materials Engineering : Statistics and probability theory used in reliability. Reliability analysis, measure and networks. Reliability prediction, modelling and testing. Concepts of preventive and corrective maintenance. Reliability based maintenance. Control and management of reliability systems. Quality and safety associated with maintenance analysis. Inventory control. Reliability based optimization.    ', '', NULL, NULL);
