INSERT INTO `courses` (`id`, `SUBJECT_CODE`, `COURSE_NUMBER`, `overview`, `prerequisites`, `created_at`, `updated_at`) VALUES(345, 'ECSE', '464', '\n      Electrical Engineering : Basic principles of planning and operating interconnected power systems with emphasis on Canadian conditions. Mathematical models for system. Steady-state analysis of power systems, load flow formulation and solution algorithms. Operating strategies, economic dispatch, voltage reactive power regulation, frequency and tie-line power control.    ', '(ecse 361)', NULL, NULL);
