INSERT INTO programs VALUES (100279,1,201609,'Fall 2016','BENG-CIVIL','MINOR','ART','Arts',NULL,NULL,'EN','Faculty of Engineering','EN','Faculty of Engineering','BENG','Bachelor of Engineering','UG','Undergraduate',0025,'Engineering - Dean''s Office','Bachelor of Engineering (B.Eng.) - Minor Arts',NULL,'Minor Adviser: Faculty Student Adviser in the Engineering Student Centre (Frank Dawson Adams Building, Room 22) B.Sc.(Arch.), B.Eng., and B.S.E. students may obtain the Arts Minor as part of their B.Eng., B.S.E., or B.Sc.(Arch.) degree by completing 24 credits, as described below.Students must select courses for this Minor in consultation with one of the Advisers indicated above. All courses in the Minor must be passed with a grade of C or better.',NULL,24,1,0,1,'1',1,0,0,0,'R','Required','Requirements','24 credits as follows:a)  At least two areas of concentration in the Faculty of Arts must be chosen, with a minimum of 6 credits in any one area.b) At least 12 credits must be at the 300 level or higher.In general, B.Eng. and B.S.E. students may use courses from the Complementary Studies lists (Group A and Group B) in their program that are offered by the Faculty of Arts to satisfy some of these requirements. No more than 9 credits of these courses can be credited toward the Arts Minor.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'EN','EN','BENG','Bachelor of Engineering',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,0025,'Engineering - Dean''s Office',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'2016-12-09 22:17:08');
INSERT INTO programs VALUES (100284,4,201609,'Fall 2016','BENG-ELEC','MAJOR','ELE','Electrical Engineering',NULL,NULL,'EN','Faculty of Engineering','EN','Faculty of Engineering','BENG','Bachelor of Engineering','UG','Undergraduate',0156,'Electrical & Computer Engr','Bachelor of Engineering (B.Eng.) - Electrical Engineering',NULL,'Program credit weight: 134-139 creditsProgram credit weight for Quebec CEGEP students: 109-114 creditsThis program gives students a broad understanding of the key principles that are responsible for the extraordinary advances in the technology of computers, micro-electronics, automation and robotics, telecommunications, and power systems. These areas are critical to the development of our industries and, more generally, to our economy. A graduate of this program is exposed to all basic elements of electrical engineering and can function in any of our client industries. This breadth is what distinguishes an engineer from, for example, a computer scientist or physicist.In addition to technical complementary courses, students in the Electrical Engineering program take general complementary courses in social sciences, administrative studies, and humanities. These courses allow students to develop specific interests in areas such as psychology, economics, management, or political science.',NULL,134,2,1,2,'1.2',1,2,0,0,'R','Required','Required Non-Departmental Courses','23 credits','* Note: FACC 100 (Introduction to the Engineering Profession) must be taken during the first year of study.',NULL,NULL,NULL,NULL,NULL,'FACC','Faculty Course','400','Engineering Professional Practice',NULL,1,NULL,'EN','EN','BENG','Bachelor of Engineering',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,0156,'Electrical & Computer Engr',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,