INSERT INTO programs VALUES (100282,3,201609,'Fall 2016','BENG-COMP','MAJOR','CPE','Computer Engineering',NULL,NULL,'EN','Faculty of Engineering','EN','Faculty of Engineering','BENG','Bachelor of Engineering','UG','Undergraduate',0156,'Electrical & Computer Engr','Bachelor of Engineering (B.Eng.) - Computer Engineering',NULL,'Program credit weight: 133-140 creditsProgram credit weight for Quebec CEGEP students: 111-115 creditsProgram credit weight for out-of-province students: 133-137 creditsThe Computer Engineering program provides students with greater depth and breadth of knowledge in the hardware and software aspects of computers. Students are exposed to both theoretical and practical issues of both hardware and software in well-equipped laboratories. Although the program is designed to meet the growing demands by industry for engineers with a strong background in modern computer technology, it also provides the underlying depth for graduate studies in all fields of Computer Engineering.In addition to technical complementary courses, students in the program take general complementary courses in social sciences, management studies, and humanities. These courses allow students to develop specific interests in areas such as psychology, economics, management, or political science.',NULL,133,15,4,3,'1.4.15',1,4,15,0,'C','Complementary','List A','9-17 credits from the following:',NULL,NULL,NULL,NULL,'11-18-2016 00:00:00',NULL,'ECSE','Electrical Engineering','420','Parallel Computing',NULL,3,NULL,'EN','EN','BENG','Bachelor of Engineering',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,0156,'Electrical & Computer Engr',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'2016-12-09 22:17:08');
