INSERT INTO programs VALUES (101982,2,201609,'Fall 2016','BENG-BIOE','MAJOR','BEN','Bioengineering',NULL,NULL,'EN','Faculty of Engineering','EN','Faculty of Engineering','BENG','Bachelor of Engineering','UG','Undergraduate',0577,'Bioengineering','Bachelor of Engineering (B.Eng.) - Bioengineering',NULL,'*** NEW PROGRAM ***Program credit weight: 141-151 creditsProgram credit weight for Quebec CEGEP students: 121-122 creditsProgram credit weight for out-of-province students: 141-142 creditsThe B.Eng.; Major in Bioengineering will 1) provide students with the ability to apply systematic knowledge of biology, physical sciences and mathematics; and sound engineering foundations in order to solve problems of a biological nature; and 2) prepare students for the broad area of bioengineering, incorporating both biology-focused biological engineering and medicine-focused biomedical engineering. Students will acquire fundamental knowledge in bioengineering-related natural sciences and mathematics, as well as in the foundations of general engineering and bioengineering.  Students will also acquire knowledge in one area of specialization of bioengineering: 1) biological materials and biomechanics; 2) biomolecular and cellular engineering; or 3) biomedical, diagnostic and high throughout screening engineering.',NULL,141,9,0,1,'9',9,0,0,0,'C','Complementary','Stream 3: Biomedical, Diagnostics and High Throughput Screening Engineering',NULL,'* When topic is appropriate, as: Microfluids and bioMEMs',NULL,NULL,NULL,NULL,NULL,'MECH','Mechanical Engineering','502','Topics in Mechanical Engineering',NULL,3,'*','EN','EN','BENG','Bachelor of Engineering',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,0577,'Bioengineering',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'2016-12-09 22:17:0